import React, { useRef, useState } from 'react'
import './tictactoe.css'
import circle_icon from '../Picture/circle.png'
import cross_icon from '../Picture/cross.png'
 
let data = ["","","","","","","","",""];
const Tictactoe = () => {

  let [count, setCount] = useState(1);
  let [lock, setLock] = useState(false);
  let checkWinner = useRef(null);
  const toggle = (e,num) => {
    if (lock) {
      return 0;
    }
    if(count%2===0){
      e.target.innerHTML = `<img src='${cross_icon}'>`;
      data[num]="x";
      setCount(++count);
    }
    else{
      e.target.innerHTML = `<img src='${circle_icon}'>`;
      data[num]="o";
      setCount(++count);
    }
    win();
  }
  const win = () => {
    if(data[1]===data[2] && data[2]===data[3] && data[3]!="" ) {winner(data[3]);}
    if(data[1]===data[5] && data[5]===data[9] && data[9]!="" ) {winner(data[9]);}
    if(data[1]===data[4] && data[4]===data[7] && data[7]!="" ) {winner(data[7]);}
    if(data[4]===data[5] && data[5]===data[6] && data[6]!="" ) {winner(data[6]);}
    if(data[7]===data[8] && data[8]===data[9] && data[9]!="" ) {winner(data[9]);}
    if(data[7]===data[5] && data[5]===data[3] && data[3]!="" ) {winner(data[3]);}
    if(data[2]===data[5] && data[5]===data[8] && data[8]!="" ) {winner(data[8]);}
    if(data[3]===data[6] && data[6]===data[9] && data[9]!="" ) {winner(data[9]);}

  }
  const winner = (winner) => {
    setLock(true);
    if(winner==="o"){checkWinner.current.innerHTML = `Winner: <img src='${circle_icon}'>`}
    else {checkWinner.current.innerHTML = `Winner: <img src='${cross_icon}'>`}
  }
  return (
    <div className='container'>
      <h1 className="title" ref={checkWinner}>Tictactoe Game</h1>
      <div className='board'>
        <div className="row1">
          <div className="boxes" onClick={(e)=>{toggle(e,1)}}>1</div>
          <div className="boxes" onClick={(e)=>{toggle(e,2)}}>2</div>
          <div className="boxes" onClick={(e)=>{toggle(e,3)}}>3</div>
        </div>
        <div className="row2">
          <div className="boxes" onClick={(e)=>{toggle(e,4)}}>4</div>
          <div className="boxes" onClick={(e)=>{toggle(e,5)}}>5</div>
          <div className="boxes" onClick={(e)=>{toggle(e,6)}}>6</div>
        </div>
        <div className="row3">
          <div className="boxes" onClick={(e)=>{toggle(e,7)}}>7</div>
          <div className="boxes" onClick={(e)=>{toggle(e,8)}}>8</div>
          <div className="boxes" onClick={(e)=>{toggle(e,9)}}>9</div>
        </div>
      </div>
      <button className="reset">Reset</button>
    </div>
  )
}
export default Tictactoe
