import React, { useState } from 'react'
import './tictactoe.css'
import circle_icon from '../Picture/circle.png'
import cross_icon from '../Picture/cross.png'
 
let data = ["","","","","","","","",""];
const Tictactoe = () => {

  let [count, setCount] = useState(1);
  let [lock, setLock] = useState(false);

  const toggle = (e,num) => {
    if (lock) {
      return 0;
    }
    if(count%2===0){
      e.target.innerHTML = `<img src='${cross_icon}'>`;
      data[num]="x";
      setCount(++count);
    }
    else{
      e.target.innerHTML = `<img src='${circle_icon}'>`;
      data[num]="o";
      setCount(++count);
    }
  }
  // const win = () => {
  //   if(data[0]===data)
  // }
  const winner = (winner) => {
    setLock(true)
  }
  return (
    <div className='container'>
      <h1 className="title">Tictactoe Game</h1>
      <div className='board'>
        <div className="row1">
          <div className="boxes" onClick={(e)=>{toggle(e,1)}}>0</div>
          <div className="boxes" onClick={(e)=>{toggle(e,2)}}>1</div>
          <div className="boxes" onClick={(e)=>{toggle(e,3)}}>2</div>
        </div>
        <div className="row2">
          <div className="boxes" onClick={(e)=>{toggle(e,4)}}>3</div>
          <div className="boxes" onClick={(e)=>{toggle(e,5)}}>4</div>
          <div className="boxes" onClick={(e)=>{toggle(e,6)}}>5</div>
        </div>
        <div className="row3">
          <div className="boxes" onClick={(e)=>{toggle(e,7)}}>6</div>
          <div className="boxes" onClick={(e)=>{toggle(e,8)}}>7</div>
          <div className="boxes" onClick={(e)=>{toggle(e,9)}}>8</div>
        </div>
      </div>
      <button className="reset">Reset</button>
    </div>
  )
}
export default Tictactoe
